import React from "react";
import Header from "../Components/Header";
import Navbar from "../Components/Navbar";
import { useState, useEffect } from "react";
import axios from "axios";
import Masonry from "react-masonry-css";
function Home() {
  const [loading, setLoading] = useState(false);
  const [listOfPost, setListOfPost] = useState([]);
  const [searchPost, setSearchPost] = useState("");
  useEffect(() => {
    axios.get("http://localhost:3001/post").then((response) => {
      console.log(response.data);
      setListOfPost(response.data);
    });
  }, []);
  return (
    <div className="bg-black h-full">
      <div className="">
        <Header />
      </div>
      <div className=" grid grid-rows-2 gap-4 grid-cols-12">
        <div className="col-span-3 ">
          <Navbar />
        </div>
        <div className="col-span-9 ml-10">
          <p className="mt-10 text-4xl text-gray-200">Feed</p>
          <div>
            <div>
              {listOfPost
                .filter((value) => {
                  if (searchPost === "") {
                    return value;
                  } else if (
                    value.title.toLowerCase().includes(searchPost.toLowerCase())
                  ) {
                    return value;
                  }
                })
                .map((listOfPost) => {
                  return (
                    <Masonry
                      breakpointCols={3}
                      className="my-masonry-grid"
                      columnClassName="my-masonry-grid_column"
                    >
                      <div key={listOfPost.id}>
                        <img
                          src={`http://localhost:3001/${listOfPost.image}`}
                        />
                        <p className="text-gray-100">{listOfPost.caption}</p>
                      </div>
                    </Masonry>
                    // <div key={listOfPost.id}>
                    //   <img src={`http://localhost:3001/${listOfPost.image}`} />
                    //   <p className="text-gray-100">{listOfPost.caption}</p>
                    // </div>
                  );
                })}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
