import React from "react";
import Header from "../Components/Header";
import Navbar from "../Components/Navbar";
import { useState, useEffect } from "react";
import axios from "axios";
import Masonry from "react-masonry-component";
import "../App.css";
import useNavigate from "react-router-dom";

function Home() {
  const [loading, setLoading] = useState(false);
  const [listOfPost, setListOfPost] = useState([]);
  const [searchPost, setSearchPost] = useState("");
  useEffect(() => {
    axios.get("http://localhost:3001/post").then((response) => {
      console.log(response.data);
      setListOfPost(response.data);
    });
  }, []);
  useEffect(() => {
    const loadPost = async () => {
      setLoading(true);
      const response = await axios.get("http://localhost:3001/post");
      setListOfPost(response.data);
      setLoading(false);
    };
    loadPost();
  }, []);

  const masonryOptions = {
    fitWidth: false,
    // columnWidth: 0,
    // gutter: 0,
    itemSelector: ".photo-item",
  };

  return (
    <div className="bg-black h-full ">
      <div className="">
        <Header />
      </div>
      <div className=" grid grid-rows-2 gap-4 grid-cols-12">
        <div className="col-span-3 ">
          <Navbar />
        </div>

        <div className="col-span-9 relative ml-20">
          <div className="flex mb-40">
            <p className="mt-10 text-4xl text-gray-200">Feed</p>
            <div className="mt-5">
              <input
                className="ml-40 bg-gray-800 w-96 h-10 rounded-md px-4"
                type="text"
                placeholder="Search ..."
                onChange={(e) => {
                  setSearchPost(e.target.value);
                }}
              ></input>
            </div>
          </div>
          <div className="mt-40">
            <Masonry
              className={"photo-list"}
              elementType={"ul"}
              options={masonryOptions}
              disableImagesLoaded={false}
              updateOnEachImageLoad={false}
              // imagesLoadedOptions={imagesLoadedOptions} // default {}
            >
              {listOfPost.map((listOfPost) => {
                return (
                  <div key={listOfPost.id} className={`photo-item`}>
                    <img
                      className="rounded-md mt-3"
                      src={`http://localhost:3001/${listOfPost.image}`}
                    />
                  </div>
                );
              })}
            </Masonry>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;

