import React from "react";
import Modal from "react-modal";
import axios from "axios";
import { useState, useEffect } from "react";
function Header() {
  const [listOfPost, setListOfPost] = useState([]);

  const [preview, setPreview] = useState(null);
  const [form, setForm] = useState({
    caption: "",
    image: "",
  });

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]:
        e.target.type === "file" ? e.target.files : e.target.value,
    });

    if (e.target.type === "file") {
      let url = URL.createObjectURL(e.target.files[0]);
      setPreview(url);
    }
  };

  const onSubmit = (e) => {
    try {
      e.preventDefault();

      const data = new FormData();
      data.set("caption", form.caption);
      data.set("image", form.image[0], form.image[0].name);

      axios
        .post("http://localhost:3001/post", data, {
          // headers: {
          //   accessToken: localStorage.getItem("accessToken"),
          // },
        })
        .then((response) => {
          console.log(response.data);
        });
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="flex">
      <div className="mt-5">
        <img
          className="h-12 ml-12 cursor-pointer"
          src="images/logo.png"
          alt=""
        />
      </div>

      <div className="mt-5 flex justify-items-end ml-40">
        <img
          className="mr-5 h-8 ml-20 cursor-pointer"
          src="images/notification.png"
          alt=""
        />
        <img className="h-8 cursor-pointer" src="images/message.png" alt="" />
        <label for="my-modal-4" className="btn modal-button bg-black">
          {/* <img
          className="h-10 mt-5 ml-20 cursor-pointer"
          src="images/create.png"
          alt=""
        /> */}
          <img src="images/create.png" />
        </label>
      </div>

      <input type="checkbox" id="my-modal-4" className="modal-toggle" />
      <label for="my-modal-4" class="modal cursor-pointer">
        <label class="modal-box relative" for="">
          <h3 class="text-lg font-bold">Share your photo or video here ...</h3>
          <div className="mt-10">
            {preview && (
              <div className="border-4 h-32 w-32">
                <img className="" src={preview} />
              </div>
            )}
          </div>
          <form onSubmit={onSubmit}>
            <div>
              <input
                onChange={handleChange}
                multiple="multiple"
                name="image"
                type="file"
              ></input>
              <input
                onChange={handleChange}
                type="text"
                name="caption"
                placeholder="your feeling ..."
                className="bg-gray-800 text-gray-200 rounded-lg mt-10 h-12 w-80"
              ></input>
            </div>

            <button for="my-modal-4" className="btn mt-10 ml-80 ">
              Share
            </button>
          </form>
        </label>
      </label>
    </div>
  );
}

export default Header;
